import React, { useContext } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import "./CovidTable.css"
import { CovidContext } from "../../context/CovidContext";

const commaNumber = require("comma-number");

const useStyles = makeStyles({
  table: {
    width: "100%",
  },
});

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: "rgb(121, 2, 2)",
    color: theme.palette.common.white,
    fontSize: 19,
    fontWeight: 400,
    height: 50

  },
  body: {
    fontSize: 19,
    fontWeight: 500
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);


function CovidTable() {

  const classes = useStyles();

  const [global, setGlobal, allCountries, setAllCountries] =
    useContext(CovidContext);
  console.log(allCountries)
  return (
    <div className="table">
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell>#</StyledTableCell>  
              <StyledTableCell><strong>Country</strong></StyledTableCell>
              <StyledTableCell>Total Cases</StyledTableCell>
              <StyledTableCell>New Cases</StyledTableCell>
              <StyledTableCell>Total Deaths</StyledTableCell>
              <StyledTableCell>New Deaths</StyledTableCell>
              <StyledTableCell>Total Recovered</StyledTableCell>
              <StyledTableCell>New Recovered</StyledTableCell>
              <StyledTableCell>Active Cases</StyledTableCell>
              <StyledTableCell>Serious, Critical</StyledTableCell>
              <StyledTableCell>Population</StyledTableCell>

            </TableRow>
          </TableHead>
          <TableBody>
            {allCountries.map((data,index) => (
              <StyledTableRow>
              <StyledTableCell>{index+1}</StyledTableCell>
                <StyledTableCell><img src={data.countryInfo.flag} height="20px" width="30px"style={{paddingRight: "20px", alignItem: "center"}}/><strong>{data.country}</strong></StyledTableCell>
                <StyledTableCell>{commaNumber(data.cases)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.todayCases)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.deaths)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.todayDeaths)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.recovered)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.todayRecovered)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.active)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.critical)}</StyledTableCell>
                <StyledTableCell>{commaNumber(data.population)}</StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}

export default CovidTable;
