import React, {useState, useEffect, createContext} from "react"

const CovidContext = createContext();

const CovidData = (props) => {
    const [global, setGlobal] = useState();
    const [allCountries, setAllCountries] = useState();
    var countries = [];

    useEffect(() => {
        fetch("https://disease.sh/v3/covid-19/all")
        .then(data => data.json())
        .then(res => setGlobal(res))
    },[])
    useEffect(() => {
        async function fetchMyData(){
            await fetch("https://disease.sh/v3/covid-19/countries")
            .then(data => data.json())
            .then(res => setAllCountries(res))
            // Object.entries(allCountries).map((item) =>
            // countries.push({
            // "title": item.country,
            // })
        }
        fetchMyData()

    },[])
        // allCountries.map((c) => console.log(c.country))

        console.log(typeof(allCountries))
    return(
        <CovidContext.Provider value={[global, setGlobal, allCountries, setAllCountries]}>
            {props.children}
        </CovidContext.Provider>
    )
}
export{
    CovidContext,
    CovidData
}
