import React, { useContext, useState } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import ReactPaginate from "react-paginate";

import "./CovidTable.css";
import { CovidContext } from "../../context/CovidContext";

const commaNumber = require("comma-number");

const useStyles = makeStyles({
  table: {
    width: "100%",
  },
});

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: "rgb(121, 2, 2)",
    color: theme.palette.common.white,
    fontSize: 19,
    fontWeight: 400,
    height: 50,
  },
  body: {
    fontSize: 19,
    fontWeight: 500,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

function CovidTable() {
  const classes = useStyles();
  const changePage = async ({ selected }) => {
    setPageNumber(selected);
    setStarting(selected);
  };

  const [global, setGlobal, allCountries, setAllCountries] =
    useContext(CovidContext);

  const [pageNumber, setPageNumber] = useState(0);
  const [interval, setInterval] = useState(10);
  const [starting, setStarting] = useState(0);
  const contPerPage = interval;
  const pagesVisited = pageNumber * contPerPage;

  var arr = [];
  var lowEnd = 0;
  var highEnd = 20;
   while (lowEnd <= highEnd) {
    arr.push((lowEnd += 5));
  }


  console.log(allCountries);
  return (
    <div className="table">
      <div className="dropdown">
        <FormControl variant="outlined" className={classes.formControl}>
          <InputLabel htmlFor="outlined-age-native-simple">Entries</InputLabel>
          <Select
            style={{
              width: "170%",
              height: "90%",
              backgroundColor: "#fcfcfc",
            }}
            native
            value={interval}
            onChange={(e) => setInterval(e.target.value)}
            label="Entries"
            inputProps={{
              name: "Entries",
              id: "outlined-age-native-simple",
            }}
          >
            {/* <option value={30}>Thirty</option> */}
            {/* {arr.map((i) => <MenuItem value={i}>{i}</MenuItem>)} */}
            {arr.map((i) => (
              <option value={i}>{i}</option>
            ))}
          </Select>
        </FormControl>
      </div>
      <div className="pagination">
        <ReactPaginate
          previousLabel={"Previous"}
          nextLabel={"Next"}
          pageCount={Math.ceil(allCountries.length / interval)}
          onPageChange={changePage}
          containerClassName={"paginationBttns"}
          previousLinkClassName={"previousBttn"}
          nextLinkClassName={"nextBttn"}
          disabledClassName={"paginationDisabled"}
          activeClassName={"paginationActive"}
        />
      </div>
      <div>
        <TableContainer component={Paper}>
          <Table className={classes.table} aria-label="customized table">
            <TableHead>
              <TableRow>
                <StyledTableCell>#</StyledTableCell>
                <StyledTableCell>
                  <strong>Country</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>Total Cases</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>New Cases</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>Total Deaths</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>New Deaths</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>Total Recovered</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>New Recovered</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>Active Cases</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>Serious, Critical</strong>
                </StyledTableCell>
                <StyledTableCell>
                  <strong>Population</strong>
                </StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {allCountries.map((data, index) => (
                <StyledTableRow>
                  <StyledTableCell>{index + 1}</StyledTableCell>
                  <StyledTableCell>
                    <img
                      src={data.countryInfo.flag}
                      height="20px"
                      width="30px"
                      style={{ paddingRight: "20px", alignItem: "center" }}
                    />
                    <strong>{data.country}</strong>
                  </StyledTableCell>
                  <StyledTableCell>{commaNumber(data.cases)}</StyledTableCell>
                  <StyledTableCell>
                    {commaNumber(data.todayCases)}
                  </StyledTableCell>
                  <StyledTableCell>{commaNumber(data.deaths)}</StyledTableCell>
                  <StyledTableCell>
                    {commaNumber(data.todayDeaths)}
                  </StyledTableCell>
                  <StyledTableCell>
                    {commaNumber(data.recovered)}
                  </StyledTableCell>
                  <StyledTableCell>
                    {commaNumber(data.todayRecovered)}
                  </StyledTableCell>
                  <StyledTableCell>{commaNumber(data.active)}</StyledTableCell>
                  <StyledTableCell>
                    {commaNumber(data.critical)}
                  </StyledTableCell>
                  <StyledTableCell>
                    {commaNumber(data.population)}
                  </StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    </div>
  );
}

export default CovidTable;
