    //   const data = await response.json();
    //   // setCourse(data.data);
    //   var temp = [];
    //   Object.entries(allCountries).map((item) =>
    //     temp.push({
    //       country: item.country
    //     })
    //   );
    //   setCountries(temp);
    //   // setCourse(course);
    // }
    // fetchMyAPI();
      console.log(countries);

  }, []);

  console.log(countries);
  return (
    <CovidContext.Provider
      value={[global, setGlobal, allCountries, setAllCountries, countries, setCountries]}
    >
      {props.children}
    </CovidContext.Provider>
  );
};
export { CovidContext, CovidData };
