import React, { useState, useEffect, createContext } from "react";

const CovidContext = createContext();

const CovidData = (props) => {
  const [global, setGlobal] = useState();
  const [allCountries, setAllCountries] = useState([]);

  useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/all")
      .then((data) => data.json())
      .then((res) => setGlobal(res));
  }, []);
  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch("https://disease.sh/v3/covid-19/countries");
      const data = await response.json();
      if (data.length > 0) {
        data.map((item) => temp.push({ title: item.country }));
      }
      temp.push({ title: "Worldwide" });
      setCountries(temp);
    }
    fetchMyAPI();
  }, []);


  return (

    <CovidContext.Provider
      value={[global, setGlobal, allCountries, setAllCountries]}
    >

      {props.children}
    </CovidContext.Provider>
  );
};
export { CovidContext, CovidData };
