import React, {useState, useContext } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import "./Header.css";

import activeImage from "../../img/active.jpg";
import deathImage from "../../img/deaths.jpg";
import recoveredImage from "../../img/recovered.jpg";

import { CovidContext } from "../../context/CovidContext";

const commaNumber = require("comma-number");

const useStyles = makeStyles({
  root: {
    width: 350,
    alignItems: "center",
  },
  media: {
    height: 200,
    maxWidth: 350,
  },
});

export default function Header() {
  const classes = useStyles();

  const [country, setCountry] = useState("")

  const [global, setGlobal] = useContext(CovidContext);
  console.log(global);
  return (
    <div className="header">
      <div className="header-top">
        <h1>Covid-19 Tracker</h1>
      </div>
      <div className="header-content">
        <div className="card">
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={activeImage}
                title="Contemplative Reptile"
              />
              <CardContent>
                <Typography
                  gutterBottom
                  variant="h5"
                  component="h2"
                  style={{ display: "flex", justifyContent: "center" }}
                >
                  Coronavirus Cases
                </Typography>
                <Typography
                  variant="body2"
                  color="textSecondary"
                  variant="h5"
                  component="h2"
                  style={{ display: "flex", justifyContent: "center" }}
                >
                  {global && commaNumber(global.cases)}
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        </div>
        <div className="card">
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={deathImage}
                title="Contemplative Reptile"
              />
              <CardContent>
                <Typography
                  gutterBottom
                  variant="h5"
                  component="h2"
                  style={{ display: "flex", justifyContent: "center" }}
                >
                  Deaths
                </Typography>
                <Typography
                  variant="body2"
                  color="textSecondary"
                  variant="h5"
                  component="h2"
                  style={{ display: "flex", justifyContent: "center" }}
                >
                  {global && commaNumber(global.deaths)}
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        </div>
        <div className="card">
          <Card className={classes.root}>
            <CardActionArea>
              <CardMedia
                className={classes.media}
                image={recoveredImage}
                title="Contemplative Reptile"
              />
              <CardContent>
                <Typography
                  gutterBottom
                  variant="h5"
                  component="h2"
                  style={{ display: "flex", justifyContent: "center" }}
                >
                  Recovered
                </Typography>
                <Typography
                  variant="body2"
                  color="textSecondary"
                  variant="h5"
                  component="h2"
                  style={{ display: "flex", justifyContent: "center" }}
                >
                  {global && commaNumber(global.recovered)}
                </Typography>
              </CardContent>
            </CardActionArea>
          </Card>
        </div>
      </div>
    </div>
  );
}
